import data from "./Data.json";
import { useState } from "react";
import cart from "./Images/assests/cart.jpg";
import "./Addcart.css";

const AddCart = () => {

  const [valueD, setvalue] =  useState(0);

  const Additem = () => {
      setvalue(valueD + 1);
  }

  const Removeitem = () => {
    if(valueD !== 0) 
    setvalue(valueD - 1);
  }

  return ( 
    <>
    <div className="headcart">
    <div className="value">{valueD}</div>
    <img src={cart} className="cart" />

    </div>

    <div>
      {
        data.map((item) => {
          return (
            <>
             <div className="pot">
             <img src={item.img} />
             <div>{item.name}</div>
             <div>Price Rs.{item.price}.00</div>
              <div value={item.available} style={{color: "red",fontWeight : "bold"}}> {item.available ? "In-Stock" : "Out-of-Stock"}</div>
             <div>{item.vendor}</div>
             <div>{item.category}</div>
             <button onClick={Additem}> + </button>
             <button onClick={Removeitem}> - </button>
             </div> <br/><br/>
            </>
          )
        })
      }
    </div>

    </>
   );
}

export default AddCart;
